import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import '../css/Contact.css';
import EmailIcon from '@mui/icons-material/Email';
import ContactPhoneIcon from '@mui/icons-material/ContactPhone';
import { useRef } from 'react';
import{ init } from '@emailjs/browser';
import emailjs from '@emailjs/browser';
import {useHistory } from "react-router-dom";

init("YAhkeZrMmmQoUvmG_");

const Contact = () => {
  let [name,setName] = useState("")
  let [email,setEmail] = useState("")
  let [message,setMessage] = useState("")
  const history = useHistory()
  const form = useRef();
  
  const handleChange = (e) => {
    e.preventDefault();
    // sessionStorage.clear();
    let num_emails=parseInt(sessionStorage.getItem("emails"));
    if(!num_emails)
    {
      sessionStorage.setItem("emails",0);
      num_emails=parseInt(sessionStorage.getItem("emails"));

      let d=new Date();
      d.setHours(d.getHours()+24);
      sessionStorage.setItem("date",d);
    }
    let initial_date=new Date(sessionStorage.getItem("date"));
    let current_date=new Date();
    if(initial_date>current_date)
    {

    }

    sessionStorage.setItem("emails",num_emails+1);
    console.log(num_emails);

        // sessionStorage.setItem()
    // emailjs.sendForm('service_rios4wr', 'template_g0vvpc2', form.current, 'YAhkeZrMmmQoUvmG_')
    //   .then((result) => {
    //       console.log(result.text);
    //       history.push("/")
    //   }, (error) => {
    //       console.log("ERROR" + error.text);
    //   });
  };

  return (<div className='contact expand-contact'>
      <form ref={form} onSubmit={handleChange} >
        <h1 className='blur-contact '>Contact</h1>
        <TextField
          className='m-2 txtField blur-contact '
          id="outlined-multiline-flexible"
          label="Name"
          placeholder='Name'
          name='name'
          required
          multiline
          maxRows={4}
          onChange={(e)=>{setName(e.target.value)}}/>
        <TextField
          className='m-2 txtField blur-contact '
          id="outlined-multiline-flexible"
          label="Email"
          placeholder='Email'
          name='email'
          type="email"
          required
          multiline
          maxRows={4}
          onChange={(e)=>{setEmail(e.target.value)}}/>
                    <div>

        <TextField
          className='txtArea m-2 mb-4 blur-contact-txt '
          id="outlined-multiline-static"
          label="Message"
          placeholder='Message'
          name='message'
          required
          multiline
          rows={4}
          onChange={(e)=>{setMessage(e.target.value)}}/>
                    </div>
          <div>
           <Button variant="contained" type='submit' className='blur-contact-button' endIcon={<SendIcon />}>
        Send
      </Button>
      </div>
      </form>
      <p className='email blur-contact-p '><EmailIcon></EmailIcon> Email: konstantinos.mazarakis25@gmail.com</p>
      <p className='phone blur-contact-p2 '><ContactPhoneIcon></ContactPhoneIcon> Phone Number: 312-763-0955</p>

      </div>
  );
}
export default Contact
