import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import '../css/Contact.css';
import EmailIcon from '@mui/icons-material/Email';
import ContactPhoneIcon from '@mui/icons-material/ContactPhone';
import emailjs from "emailjs-com"

const Contact = () => {
  let [name,setName] = useState("")
  let [email,setEmail] = useState("")
  let [message,setMessage] = useState("")
  const form = useRef();
  
  const handleChange = (e) => {
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_g0vvpc2', e.current, 'YAhkeZrMmmQoUvmG_')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log("ERROR" + error.text);
      });
  };

  return (<div className='contact'>
      <form ref={form} onSubmit={handleChange} >
        <h1>Contact</h1>
        <TextField
          className='m-2 txtField'
          id="outlined-multiline-flexible"
          label="Name"
          placeholder='Name'
          name='name'
          required
          multiline
          maxRows={4}
          onChange={(e)=>{setName(e.target.value)}}/>
        <TextField
          className='m-2 txtField'
          id="outlined-multiline-flexible"
          label="Email"
          placeholder='Email'
          name='email'
          type="email"
          required
          multiline
          maxRows={4}
          onChange={(e)=>{setEmail(e.target.value)}}/>
                    <div>

        <TextField
          className='txtArea m-2 mb-4'
          id="outlined-multiline-static"
          label="Message"
          placeholder='Message'
          name='message'
          required
          multiline
          rows={4}
          onChange={(e)=>{setMessage(e.target.value)}}/>
                    </div>
          <div>
           <Button variant="contained" type='submit' endIcon={<SendIcon />}>
        Send
      </Button>
      </div>
      </form>
      <p className='email'><EmailIcon></EmailIcon> Email: konstantinos.mazarakis25@gmail.com</p>
      <p className='phone'><ContactPhoneIcon></ContactPhoneIcon> Phone Number: 312-763-0955</p>

      </div>
  );
}
export default Contact
